var request = require('request');

var readme_url = 'https://github.com/rhysd/Tui/blob/master/README.md';
var opts = {
    url: readme_url,
    method: 'HEAD',
}

request(opts, function(err, res, _) {
    if (err) {
        console.log('error: ' + err.message);
        return;
    }
    console.log(JSON.stringify(res, null, 2));
});
